import{r as o,j as e,R as m,e as g}from"./index-24bc01bb.js";import{q as k,N as b,G as y}from"./Nav-f33bba3a.js";import{I as w}from"./ImageViewer-a9c2f80f.js";import{u as v}from"./useWindowSize-1fe33602.js";import{C as p,R as f}from"./react-markdown-cdee3cc8.js";import"./LinearProgress-dd3170c5.js";import"./index-e81dcd98.js";const A=[{term:"actuator",definition:"A component at the back of the plug that throws the bolt or unlocks the shackle when the plug is turned.",media:{title:"By: drZongo",subtitle:"CC BY-NC-SA 4.0",thumbnailUrl:"https://live.staticflickr.com/65535/53448597719_e97f3e0688_w_d.jpg",fullUrl:"https://www.flickr.com/photos/lpubeltapp/53448597719/in/album-72177720313869627/",fullSizeUrl:"https://live.staticflickr.com/65535/53448597719_20ec5a93d1_o.jpg"}},{term:"anti-drill element",definition:"Component in a lock made of hardened steel to slow down or prevent drilling attacks. Various forms of drill protection are used by different manufacturers. It may be a part of the lock body itself, a steel pin or ball bearing inserted in the lock body or plug, or the pins themselves."},{term:"ball bearing locking mechanism (padlocks)",definition:"Unshimmable mechanism in padlocks where ball bearings are pushed into the shackle to prevent opening."},{term:"barrel driver pin",definition:"Name given to any driver pin with a shape similar to a “barrel”, having a portion near the extremities with a reduction in diameter, then flaring out at the end. Usually referring to the ASSA version, can also be used for other varieties like the DOM barrels."},{term:"barrel key / pin key",definition:"A key for a lever lock where the shaft of the key is a solid cylinder."},{term:"bible",definition:"The part of a pin tumbler lock where the driver pins and springs reside."},{term:"bible cover",definition:"A cover plate that is sometimes on top of the bible. Can be removed for gutting.",media:{title:"By: mgsecure",subtitle:"CC BY-NC-SA 4.0",thumbnailUrl:"https://live.staticflickr.com/65535/53448597624_c2ae33dfe6_w_d.jpg",fullUrl:"https://www.flickr.com/photos/lpubeltapp/53448597624/in/album-72177720313869627/",fullSizeUrl:"https://live.staticflickr.com/65535/53448597624_ac642550d8_o.jpg"}},{term:"binding",definition:"The resistance to motion of an unset pin due to friction, usually against the sides of the bible chamber, when tensioning."},{term:"bitting",definition:"The unique cuts in a key that move tumblers to specified places when inserted.",media:{title:"By: mgsecure",subtitle:"CC BY-NC-SA 4.0",thumbnailUrl:"https://live.staticflickr.com/65535/53448877681_ab5369b5ea_w_d.jpg",fullUrl:"https://www.flickr.com/photos/lpubeltapp/53448877681/in/album-72177720313869627/",fullSizeUrl:"https://live.staticflickr.com/65535/53448877681_3b3f7bae48_o.jpg"}},{term:"bitting code",definition:"An alphanumeric code that can be provided to a locksmith to cut a key to a desired shape. Some codes may be found on the keys themselves, other times in key cards that are obtained along with the lock."},{term:"blade (key)",definition:"The part of a pin tumbler / dimple key that enters the lock."},{term:"BOK",definition:"(1) Bottom Of the Keyway - the area of the keyway opposite to the pins in a normal pin tumbler; corresponds to the inferior area of the keyway when the bible is upwards; in regular euro cylinder installation, with the bible downwards, will be the top part of the keyway. (2) BOK (tensioners): name colloquially given for tensioners used in the bottom of the keyway"},{term:"bolt",definition:"The part of a lock that extends out from a door into the door frame, retracted by turning the key."},{term:"bow",definition:"The part of a key which is held, at the end of the blade/shaft."},{term:"brick",definition:"To prevent a lock from functioning or gutting without physical damage, for eg. by dropping a driver pin in the plug."},{term:"cam",definition:"The piece at the back of a plug that rotates with the movement of the plug. See actuator."},{term:"challenge lock",definition:"A lock modified from stock by a picker as a picking challenge to be passed around in the community."},{term:"chamber",definition:"Milled cyllindric-shaped hole where pins are inserted. Can be plug chambers or bible chambers. "},{term:"CL",definition:"Short for Challenge Lock."},{term:"comb pick",definition:"A tool used to overlift all pin stacks, opening cheaply made locks."},{term:"control key",definition:"A key used to remove a lock from its housing. Makes it easy to rekey a lock simply by swapping cores."},{term:"core",definition:"The plug and housing of a lock. Often used as an interchangeable word for plug."},{term:"core bias",definition:"The use of springs or similar to keep the plug at a neutral position."},{term:"core shims",definition:"Flat pieces of metal used for core shimming."},{term:"counter-rotation",definition:"To rotate the plug towards neutral from a false set. Can be caused by the lock elements, usually spools, or done manually."},{term:"curtain",definition:"A component in a lever lock to only allow a small area of space within the lock to be accessible at any one time."},{term:"cut",definition:"A single part of the bitting to interface with a single element."},{term:"cutaway",definition:"A lock that has been modified by removing metal so that one can see how it operates."},{term:"CW / CCW",definition:"Clockwise/Anticlockwise, usually tension directions."},{term:"cylinder",definition:"See core."},{term:"DD",definition:"Short for Disc Detainer."},{term:"DD Pick / Disc Pick",definition:"A pick that has tensioner and tip rotating independently co-axially, used to pick disc detainer locks."},{term:"depth / height",definition:"The height to which a specific part of the bitting is cut."},{term:"dial (lock)",definition:"The part of a combination lock that is rotated"},{term:"dial (picking)",definition:"To pick a lock with unsprung tumblers such as a disc detainer by moving tumblers directly to their correct position, memorising the key."},{term:"dimple (lock)",definition:"Generally used to identify a certain subtype of pin tumblers in which the coding is marked on the key mainly on the flat side; this distinction is useful from a tool choice perspective; although used to refer to the lock type itself, this isn’t technically correct, as the word “dimple” actually designates the round markings on the wide face of the key where the keypins make contact.",media:{title:"By: mgsecure",subtitle:"CC BY-NC-SA 4.0",thumbnailUrl:"https://live.staticflickr.com/65535/53449197029_919ee19a8b_w_d.jpg",fullUrl:"https://www.flickr.com/photos/lpubeltapp/53449197029/in/album-72177720313869627/",fullSizeUrl:"https://live.staticflickr.com/65535/53449197029_e23183e517_o.jpg"}},{term:"dimple pick",definition:"See flag."},{term:"disc detainer (lock / locking mechanism)",definition:"A type of lock that uses unsprung rotating discs and a sidebar. The key repositions the discs so that gates align, the sidebar drops into them and the plug can rotate."},{term:"drill pin",definition:"A cylinder that is in the centre of the keyway of a lever lock that uses a pipe key, reducing access and centering the key."},{term:"driver pin",definition:"The pins that sit usually at shearline in a pin tumbler lock, preventing the rotation of the plug, and sit between a keypin and a spring. They must be fully lifted into the bible to open the lock. Also known as top pin when bible is upwards. Can include security features."},{term:"element",definition:"Movable part of the locking mechanism preventing the lock from opening. See tumbler."},{term:"euro (lock / cylinder)",definition:"A lock type common in Europe, also called the euro profile cylinder."},{term:"face",definition:"The visible part of a lock where the key is inserted."},{term:"false gate",definition:"A gate on a tumbler which is not deep enough for the core to turn if the sidebar falls in."},{term:"false set",definition:"The small rotation of a plug as elements (particularly spools or false gates) look partially set, that does not allow the lock to open."},{term:"feedback",definition:"The auditory, visual, and tactile sensations from a lock during picking."},{term:"fingerpin",definition:"Subtype of sidepin with extended prong that interacts with the key, and vertical rotational as well as vertical lift coding (must be lifted and rotated). Generally associated to a sidebar.",media:{title:"By: mgsecure",subtitle:"CC BY-NC-SA 4.0",thumbnailUrl:"https://live.staticflickr.com/65535/53448885038_34827b1046_w_d.jpg",fullUrl:"https://www.flickr.com/photos/lpubeltapp/53448885038/in/album-72177720313869627/",fullSizeUrl:"https://live.staticflickr.com/65535/53448885038_db6a86e52e_o.jpg"}},{term:"flag",definition:"A lock pick used for dimple locks, shaped like a rod with a protrusion at the side of the tip. Also called dimple pick."},{term:"follower",definition:"A long cylinder used to push the plug out when disassembling a lock, holding the driver pins in place inside the bible."},{term:"front tension",definition:"A tension method used for disc detainer locks, applying rotational force at the face of the lock."},{term:"gate",definition:"A divot on a slider or disc that allows the sidebar to fall into it."},{term:"grubbing",definition:"To tap and insert grub/set screws into the top of a bible, acting as individual bible covers for each chamber."},{term:"gutting",definition:"To disassemble a lock into its individual parts."},{term:"hook",definition:"A tool used for single pin picking, shaped like a thin flat metal bar with a raised hook at the end to lift pins."},{term:"housing",definition:"The part of a lock the plug is placed into, containing the bible."},{term:"HPC",definition:'(1) High Pin Count (locks) - although the use can be flexible, one acceptable definition is "a lock with 8 or more active and independent pin stacks, from at least 2 different rows from different directions". (2) brand of picks sold mainly in USA',media:{title:"By: mgsecure",subtitle:"CC BY-NC-SA 4.0",thumbnailUrl:"https://live.staticflickr.com/65535/53448878226_b6d1133493_w_d.jpg",fullUrl:"https://www.flickr.com/photos/lpubeltapp/53448878226/in/album-72177720313869627/",fullSizeUrl:"https://live.staticflickr.com/65535/53448878226_a7a8c7137c_o.jpg"}},{term:"impressioning",definition:"A method for creating a key without gutting or picking, using binding and jiggle tests and a file."},{term:"jiggle",definition:"The allowed motion of a set element due to lock tolerances. See Jiggle Test."},{term:"jiggle test",definition:"A test to see whether an element is set or not by seeing if it has a small jiggle to it, corresponding to the spring movement. If there is none, it is binding or under/overset."},{term:"key code",definition:"See bitting code."},{term:"key pin (or keypin)",definition:"The pins in the plug in a pin tumbler lock, that are lifted by the key to lift the driver pins. Their size corresponds to the code for a key cut. When a key with the correct cut size in that position is inserted, the keypin lifts the driver pin just above the shear line. Also known as bottom pin when the bible is upwards. Can include security features."},{term:"keyway",definition:"The cavity in which the key is placed."},{term:"KIK",definition:"Short for Key in Knob cylinder, a type of cylinder often placed in knobsets or padlocks."},{term:"lever (element)",definition:"(1) Coding element shaped like a flat piece of metal, that rotates about one point, interacting with the locking elements, which can be, for example. a sidebar or a talon, depending on the type of lever lock. (2) Italian levers (and silmilar types) are technically not levers as they don't rotate about one point, but are traditionally called levers."},{term:"lever (lock / locking mechanism)",definition:"type of lock that uses levers as locking mechanism. The levers generally interact with a talon, an extended piece of metal of the bolt, usually through gates. When levers are lifted to the right height, the key pulls the bolt's talon into the levers' gates."},{term:"lever pack",definition:"A term for all the levers in a given lock."},{term:"lever pick",definition:"A tool used to pick levers, either similar to a disc detainer pick or an L shaped wire."},{term:"lever/sidebar (locking mechanism)",definition:"type of locking mechanism where levers interact with a sidebar, generally through gates, instead of a talon, allowing the sidebar to drop into the gates when the correct key is inserted and the plug to rotate"},{term:"LFIC",definition:"A type of lock that can be removed from its housing with one key, and opened with another. See SFIC."},{term:"lock",definition:"I hope you know what this means."},{term:"LOTO",definition:"Lock Out Tag Out locks, used for maintenance work and safety applications. Should NEVER be picked in the field, are often cheap."},{term:"MACS",definition:"Maximum Adjacency Cut Standards. A rule for maximum height difference between adjacent bitting heights on a key. These vary with each lock model. Violation of MACS does not result in an unusable lock but does increase probablility that the key will not work properly in the lock."},{term:"master ring",definition:"A unique mechanism similar to SFIC, but allowing 2 unique control keys instead of a control and operating key."},{term:"master wafer",definition:"Very thin pins used to facilitate mastering in pin tumbler locks."},{term:"mastering",definition:"Adding (or removing) components within a lock to allow more than 1 unique key to work in the same lock."},{term:"mortise (cylinder)",definition:"A cylinder that is designed to screw into a mortise lock."},{term:"mortise (lock)",definition:"A lock designed to fit in a section of a door that has been mortised. Also called mortice (UK)."},{term:"music wire",definition:"A type of wire used in pianos, that is a good material to construct lever lock picks and similar tools."},{term:"operating key",definition:"A key used to turn the plug. Only used when a control key also exists."},{term:"oval cylinder",definition:"A lock housing that has oval cross-section."},{term:"overset",definition:"To lift an element too far past where it needs to be, preventing the lock from opening."},{term:"pin in pin",definition:"A key or driver pin that has a smaller pin inside that moves independently."},{term:"pin tumbler",definition:"Any lock using stacks of pins composed by a coding keypin and a driver pin. Although generally used for locks with vertical keyways with pins coming from the bible side, and opened with vertical keys coded on one edge, the group technically includes also the so-called dimple locks and some tubular pump locks."},{term:"PIP",definition:"See Pin in Pin."},{term:"pipe key",definition:"A lever key with hollow cylindrical shaft, to interface with a drill pin."},{term:"plate key",definition:"A key made out of flat sheet metal."},{term:"plug",definition:"The central part of a lock cylinder that rotates, where the key is inserted.-------"},{term:"plug retention",definition:"The use of circlips or similar to prevent a plug from falling out by itself or when pulled."},{term:"pp",definition:"See Progressive Pinning."},{term:"profile",definition:"The cross-sectional shape of a key or keyway."},{term:"prog",definition:"See Progressive Pinning."},{term:"progressive pinning",definition:"Learning how to open a lock by removing tumblers, slowly replacing them as you pick until fully pinned."},{term:"prybar",definition:"A tool generally used for TOK tensioning."},{term:"pump / push (lock / locking mechanism)",definition:"a pump lock or push lock is a type of lock that tensions and opens by first pushing the key into the face of the lock before rotating. This includes, for example, most tubular locks (many of which are also types of pin tumblers), but also other locks that use sliders and sidebars as locking elements."},{term:"rake",definition:"A tool that opens basic pin tumbler locks by random motion."},{term:"rear tension",definition:"To tension a disc detainer or similar lock by reaching in with a tool and turning an element at the back of the lock."},{term:"reverse sidebar",definition:"A sidebar that is spring biased into the plug with weaker springs than the sliders, preventing setting and feedback."},{term:"rim cylinder",definition:"A cylinder similar to a mortise cylinder to be installed directly in a door, with the use of screws."},{term:"serrated",definition:"A type of pin or other element that has shallow grooves etched into it, causing confusing feedback."},{term:"set",definition:"To lift an element to its required position for the lock to open, and for it to stay there due to tension."},{term:"SFIC",definition:"A type of lock that opens with one key and can be removed from its housing with another. See LFIC."},{term:"shackle",definition:"The usually hardened steel part of a padlock that secures a chain or similar."},{term:"shaft (key)",definition:"The cylindrical part of a lever lock key where the bitting flag attaches."},{term:"shaft / shank (pick)",definition:"The part of a hook, rake, or other pick between the tip and handle."},{term:"shearline / shear line",definition:"A line representing the height to which pins must be lifted for the lock to open."},{term:"shimming (core)",definition:"To insert a thin piece of metal (shim) between the plug and the housing. Generally used in locksport to prevent the driver pins to drop into holes in the plug that might brick the lock during disassembling. Can also be used to open and disassemble an unkeyed lock, by sliping the shim between the keypin and driver pin on every chamber from the back of the lock, while lifting each stack with a pick. In rare cases, one can shim a padlock from the front to open it."},{term:"shimming (padlock)",definition:"To insert a shim between the shackle and body of a padlock without ball bearing mechanism, opening the lock without picking."},{term:"shoulder",definition:"The part of a key near the bow that collides with the face of the lock to prevent over-insertion."},{term:"sidebar",definition:"A piece of metal that interfaces with another locking element (eg. levers, sliders, other sidepins) and blocks rotation until the key is inserted."},{term:"sidepin",definition:"Pin or pin-like element that works as a secondary locking mechanism. Can include various pin types like fingerpins, sliders, active and passive pin elements, among others with no specific designation.",media:{title:"By: mgsecure",subtitle:"CC BY-NC-SA 4.0",thumbnailUrl:"https://live.staticflickr.com/65535/53448885038_34827b1046_w_d.jpg",fullUrl:"https://www.flickr.com/photos/lpubeltapp/53448885038/in/album-72177720313869627/",fullSizeUrl:"https://live.staticflickr.com/65535/53448885038_db6a86e52e_o.jpg"}},{term:"single pin picking",definition:"Picking a lock by moving one element at a time under tension."},{term:"slider",definition:"Element that moves in only one direction in the lock, usually without pushing directly other pins or elements, and is kept rotationally locked to maintain correct contact with the functional elements it interacts with. It can be the main or secondary locking mechanism of a lock. Can be Sprung or Unsprung. Generally considered only for those that interact with a sidebar, although the actual definition and distinction with Wafers is controversial."},{term:"spacer",definition:"Sheet metal between 2 discs or levers that remains stationary, allowing the rotation of one element to not affect adjacent ones."},{term:"spool",definition:"A pin shaped like a spool of thread, with wider ends than the middle."},{term:"SPP",definition:"See Single Pin Picking."},{term:"stack",definition:"The combined key pin, driver pin, and spring within 1 chamber."},{term:"standard pin",definition:"A pin which is shaped like a regular cylinder (with a bevel if it is a keypin)."},{term:"talon",definition:"The part of a bolt in a lever lock that slides into the levers when unlocked."},{term:"tensioner",definition:"A tool used to apply rotational force to a plug in picking."},{term:"throw",definition:"The movement of a bolt as the key rotates."},{term:"TOK",definition:"Top of the Keyway. The area of the keyway on the same side of the pins in a normal pin-tumbler lock. Is positioned at the top of the lock when the bible is upwards. In regular euro cylinder installation with the bible downwards, it will be the bottom part of the keyway."},{term:"TOK tensioner",definition:"The nickname for a tensioner used in the top of the keyway. (see [TOK](/#/glossary?term=TOK))"},{term:"top gutting",definition:"Gutting a lock by removing covers or grubs on the bible and emptying pin stacks out from the top of the bible."},{term:"true gate",definition:"A gate on a tumbler where if the sidebar falls in, it is sufficiently deep for the plug to turn."},{term:"tumbler",definition:"A moving element on a lock that prevents opening until the correct key is inserted."},{term:"underset",definition:"A partially set element that is between its resting position and where it needs to be."},{term:"wafer (element)",definition:'(1) A tumbler shaped like a flat piece of metal, generally found in many mailbox locks. (2) A tumbler that slides in one direction, that is the size of the plug chamber it occupies. At rest it blocks the rotation of the plug by locking into grooves in the lock housing. As the correct key is inserted, it retracts into the plug completely. This definition does not consider the "wafer-shaped" condition of the tumbler. Although somewhat controversial for that reason, it is the most accepted definition. (3) For some, a subset of sliders that block the rotation of the plug by locking into grooves in the housing. This is also somewhat controversial.'},{term:"wafer (lock)",definition:"A lock that uses wafers as main locking mechanism."},{term:"ward",definition:"A piece of metal used to block the rotation of a blank in a lever or warded lock."},{term:"warded lock",definition:"A lock where the actuator can be directly reached by the key, blocked by unmoving obstructions."},{term:"warding",definition:"Protruding metal in a keyway to make picking more difficult and restrict which key shapes can enter the plug."},{term:"wiper (insert)",definition:"Steel insert from a windscreen wiper, often used to make tensioners - Or the tensioner made in the shape of such, usually BOK tensioners."},{term:"z-bar",definition:"A tensioner shaped like a Z, with long prongs often used for dimple locks."}];function T({entry:t}){const{width:n}=v(),r=n<500?110:150,{term:c,media:{thumbnailUrl:s}={}}=t,[h,l]=o.useState(!1),a=o.useCallback(()=>{l(!0)},[]),d=o.useCallback(()=>{l(!1)},[]);return s?e.jsxs(m.Fragment,{children:[e.jsx("div",{style:{margin:"6px 0px 12px 20px",float:"right",textAlign:"center",fontSize:"0.85rem"},children:e.jsx("img",{alt:c,src:s,style:{width:r,cursor:"pointer"},onClick:a})}),h&&e.jsx(w,{media:[t.media],openIndex:0,onClose:d})]}):null}function C({entry:t,highlighted:n}){const i=o.useRef(),[r,c]=o.useState(!1),s={maxWidth:700,marginLeft:"auto",marginRight:"auto",padding:"0px 20px",borderRadius:0,backgroundColor:n?"#222":void 0};o.useEffect(()=>{n&&i&&!r&&(c(!0),setTimeout(()=>{window.scrollTo({left:0,top:i.current.offsetTop-74,behavior:"smooth"})},0))},[n,t,r]);const h=o.useMemo(()=>{var d;let a=`\`${t.term}\`. ${t.definition}`;if((d=t.media)!=null&&d.title){const u=t.media.title.charAt(0).toLowerCase()+(t==null?void 0:t.media.title.slice(1));a+=` (Photo ${u})`}return a},[t]),l={code({children:a}){return e.jsx("span",{style:{fontWeight:700,fontSize:"1.1rem",marginBottom:4,color:"#fff"},children:a})}};return e.jsx(p,{style:s,ref:i,children:e.jsxs("div",{style:{color:"#ddd"},children:[e.jsx(T,{entry:t}),e.jsx(f,{components:l,style:{},children:h})]})})}const S="Definitions can be tricky, and this is certainly true when it comes to locks. We've done our best to come up with accurate, concise definitions for terms **as they are generally used in Lock Pickers United contexts**. Many terms have multiple meanings and connotations. Regional differences, between Europe and the US for example, can be significant. We've had spirited debates involving very experienced locksport enthusiasts and locksmiths over seemingly simple definitions ... language is evolving and some of the terminology here is still being settled. &nbsp; &nbsp; *Enjoy!*";function x(){const t={maxWidth:700,marginLeft:"auto",marginRight:"auto",padding:"6px 20px",borderBottom:"1px solid #333",borderTop:"1px solid #333",borderRadius:0};return e.jsx(p,{style:t,children:e.jsx(f,{children:S})})}function j(){const t=g(),n=o.useMemo(()=>{const{term:i}=k.parse(t.search);return i},[t.search]);return e.jsxs("div",{style:{margin:8,paddingBottom:32,marginLeft:"auto",marginRight:"auto",marginTop:16,marginButtom:16},children:[e.jsx(x,{}),A.map(i=>e.jsx(C,{entry:i,highlighted:n===i.term},i.term)),e.jsx("img",{alt:"glossary",src:"https://images.lpubelts.com/i/glossary.gif",width:0,height:0})]})}function z(){return e.jsxs(m.Fragment,{children:[e.jsx(b,{title:"Glossary"}),e.jsx(j,{}),e.jsx(y,{})]})}export{z as default};
